import React, { useState, useEffect } from 'react';
import { 
  ChevronDownIcon,
  ChevronUpIcon,
  MagnifyingGlassIcon,
  PencilSquareIcon,
  PlusIcon
} from '@heroicons/react/24/outline';
import { useAuth } from '../context/AuthContext.tsx';

// Define the tracker data structure
interface TrackerDescription {
  id: number;
  description: string;
  url?: string; // Optional URL if we want to link to external resources
  editedBy?: string;
  lastEdited?: Date;
}

interface TrackerTool {
  id: number;
  name: string;
  descriptions: TrackerDescription[];
  isOpen: boolean; // For dropdown state
}

const Trackers: React.FC = () => {
  const { isAdmin } = useAuth();
  const [trackers, setTrackers] = useState<TrackerTool[]>([]);
  const [filteredTrackers, setFilteredTrackers] = useState<TrackerTool[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(5);
  const [editMode, setEditMode] = useState({ toolId: -1, descriptionId: -1 });
  const [editText, setEditText] = useState('');
  const [showAddModal, setShowAddModal] = useState(false);
  const [newToolName, setNewToolName] = useState('');
  const [newDescription, setNewDescription] = useState('');
  const [selectedTool, setSelectedTool] = useState<number | null>(null);
  const [successMessage, setSuccessMessage] = useState<string | null>(null);

  // Initialize with data from the image
  useEffect(() => {
    // Simulating API call with the data from the image
    const fetchData = () => {
      setLoading(true);
      try {
        const data: TrackerTool[] = [
          {
            id: 1,
            name: 'Inventory Details',
            isOpen: false,
            descriptions: [
              { id: 1, description: 'LTF Application with Private IP details' },
              { id: 2, description: 'LTF Application with Public IP details & URLs' },
              { id: 3, description: 'Asset Details - Network Devices for all locations' },
              { id: 4, description: 'Asset Details - Servers for On Prem and GCP' },
              { id: 5, description: 'Endpoint Details ( Laptop & Desktop )' },
              { id: 6, description: 'Branch Location VLAN Segment Details' },
              { id: 7, description: 'Branch Location IP subnet Details' },
              { id: 8, description: 'Saas Based tool IP Details' }
            ]
          },
          {
            id: 2,
            name: 'Netskope',
            isOpen: false,
            descriptions: [
              { id: 9, description: 'Email Exception Sheet' },
              { id: 10, description: 'Whatsapp Access Sheet' },
              { id: 11, description: 'Generative AI Sheet' },
              { id: 12, description: 'Exception URL List' },
              { id: 13, description: 'Resigned User List' },
              { id: 14, description: 'BH User List' },
              { id: 15, description: 'Web Proxy Checklist' }
            ]
          },
          {
            id: 3,
            name: 'ZPA VPN',
            isOpen: false,
            descriptions: [
              { id: 16, description: 'ZPA Server or FQDN Access Sheet' },
              { id: 17, description: 'ZPA Groups Sheet' }
            ]
          },
          {
            id: 4,
            name: 'Tenable',
            isOpen: false,
            descriptions: [
              { id: 18, description: 'Server Patching' }
            ]
          },
          {
            id: 5,
            name: 'Advisory',
            isOpen: false,
            descriptions: [
              { id: 19, description: 'All Advisory' }
            ]
          },
          {
            id: 6,
            name: 'Honey-Pot',
            isOpen: false,
            descriptions: [
              { id: 20, description: 'Honey Pot Block IP\'s' }
            ]
          },
          {
            id: 7,
            name: 'SIEM',
            isOpen: false,
            descriptions: [
              { id: 21, description: 'SIEM Daily Alerts Tracker' },
              { id: 22, description: 'SIEM Block IP Details' },
              { id: 23, description: 'SIEM Integrated Device IP Details' },
              { id: 24, description: 'SIEM Use Cases stimulation' }
            ]
          },
          {
            id: 8,
            name: 'SOC',
            isOpen: false,
            descriptions: [
              { id: 25, description: 'SOC Team Vendors Repository' },
              { id: 26, description: 'SOC Team Device Details' }
            ]
          },
          {
            id: 9,
            name: 'Internal LTF Infosec Team',
            isOpen: false,
            descriptions: [
              { id: 27, description: 'LTF INFRA Team Support details' },
              { id: 28, description: 'Samson Santosh Core IT and Application Support Team' },
              { id: 29, description: 'Dhananjay Singh Digital and Application Engineering Team' },
              { id: 30, description: 'GEC members details' }
            ]
          },
          {
            id: 10,
            name: 'Escalation Matrix',
            isOpen: false,
            descriptions: [
              { id: 31, description: 'Bussiness Fuction BL Level' },
              { id: 32, description: 'OEM escalation Matirx.' },
              { id: 33, description: '1. Netskope' },
              { id: 34, description: '2. Zcsalar' },
              { id: 35, description: '3. Iraje' },
              { id: 36, description: '4. TCL' },
              { id: 37, description: '5. Vinca' },
              { id: 38, description: '6. Protechmanize' },
              { id: 39, description: '7. Tenable' }
            ]
          }
        ];

        setTrackers(data);
        setFilteredTrackers(data);
        setError(null);
      } catch (err) {
        console.error('Error fetching trackers:', err);
        setError('Failed to load tracker data. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Filter trackers based on search term
  useEffect(() => {
    if (searchTerm === '') {
      setFilteredTrackers(trackers);
    } else {
      const filtered = trackers.map(tool => {
        // Filter descriptions within the tool
        const matchingDescriptions = tool.descriptions.filter(desc => 
          desc.description.toLowerCase().includes(searchTerm.toLowerCase())
        );

        // If there are matching descriptions, include the tool with only those descriptions
        if (matchingDescriptions.length > 0) {
          return {
            ...tool,
            descriptions: matchingDescriptions,
            isOpen: true // Auto-expand when filtering
          };
        }

        // Check if tool name matches
        if (tool.name.toLowerCase().includes(searchTerm.toLowerCase())) {
          return { ...tool, isOpen: true }; // Auto-expand when filtering
        }

        return null;
      }).filter(Boolean) as TrackerTool[];

      setFilteredTrackers(filtered);
    }
  }, [searchTerm, trackers]);

  // Toggle dropdown for a specific tool
  const toggleDropdown = (id: number) => {
    setFilteredTrackers(prevTrackers => 
      prevTrackers.map(tracker => 
        tracker.id === id ? { ...tracker, isOpen: !tracker.isOpen } : tracker
      )
    );
  };

  // Handle search input change
  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(e.target.value);
    setCurrentPage(1); // Reset to first page when searching
  };

  // Handle items per page change
  const handleItemsPerPageChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    setItemsPerPage(parseInt(e.target.value));
    setCurrentPage(1); // Reset to first page when changing items per page
  };

  // Start editing a description
  const handleEditStart = (toolId: number, descriptionId: number, currentText: string) => {
    setEditMode({ toolId, descriptionId });
    setEditText(currentText);
  };

  // Save edited description
  const handleEditSave = (toolId: number, descriptionId: number) => {
    setTrackers(prev => prev.map(tool => {
      if (tool.id === toolId) {
        const updatedDescriptions = tool.descriptions.map(desc => 
          desc.id === descriptionId ? { ...desc, description: editText } : desc
        );
        return { ...tool, descriptions: updatedDescriptions };
      }
      return tool;
    }));

    // Reset edit mode
    setEditMode({ toolId: -1, descriptionId: -1 });
    setEditText('');

    // Show success message
    setSuccessMessage('Description updated successfully');
    setTimeout(() => setSuccessMessage(null), 3000);
  };

  // Cancel editing
  const handleEditCancel = () => {
    setEditMode({ toolId: -1, descriptionId: -1 });
    setEditText('');
  };

  // Add new tool
  const handleAddTool = () => {
    if (!newToolName.trim()) return;

    const newTool: TrackerTool = {
      id: Date.now(),
      name: newToolName,
      descriptions: [],
      isOpen: false
    };

    setTrackers(prev => [...prev, newTool]);
    setNewToolName('');
    setShowAddModal(false);

    // Show success message
    setSuccessMessage('Tool added successfully');
    setTimeout(() => setSuccessMessage(null), 3000);
  };

  // Add new description to existing tool
  const handleAddDescription = () => {
    if (!newDescription.trim() || selectedTool === null) return;

    const newDesc: TrackerDescription = {
      id: Date.now(),
      description: newDescription
    };

    setTrackers(prev => prev.map(tool => 
      tool.id === selectedTool 
        ? { ...tool, descriptions: [...tool.descriptions, newDesc] } 
        : tool
    ));

    setNewDescription('');
    setSelectedTool(null);
    setShowAddModal(false);

    // Show success message
    setSuccessMessage('Description added successfully');
    setTimeout(() => setSuccessMessage(null), 3000);
  };

  // Pagination calculations
  const totalTools = filteredTrackers.length;
  const indexOfLastTool = currentPage * itemsPerPage;
  const indexOfFirstTool = indexOfLastTool - itemsPerPage;
  const currentTools = filteredTrackers.slice(indexOfFirstTool, indexOfLastTool);
  const totalPages = Math.ceil(totalTools / itemsPerPage);

  // Generate pagination controls
  const renderPagination = () => {
    const pageNumbers = [];
    const maxPagesToShow = 5;

    let startPage = Math.max(1, currentPage - Math.floor(maxPagesToShow / 2));
    let endPage = Math.min(totalPages, startPage + maxPagesToShow - 1);

    if (endPage - startPage + 1 < maxPagesToShow && startPage > 1) {
      startPage = Math.max(1, endPage - maxPagesToShow + 1);
    }

    for (let i = startPage; i <= endPage; i++) {
      pageNumbers.push(i);
    }

    return (
      <div className="mt-4 flex items-center justify-between">
        <div>
          <span className="text-sm text-gray-700 dark:text-gray-300">
            Showing <span className="font-medium">{indexOfFirstTool + 1}</span> to{' '}
            <span className="font-medium">{Math.min(indexOfLastTool, totalTools)}</span> of{' '}
            <span className="font-medium">{totalTools}</span> tools
          </span>
        </div>

        <div className="flex items-center space-x-1">
          <button
            onClick={() => setCurrentPage(Math.max(1, currentPage - 1))}
            disabled={currentPage === 1}
            className="px-2 py-1 text-sm border rounded text-gray-700 dark:text-gray-300 border-gray-300 dark:border-gray-600 
              hover:bg-gray-50 dark:hover:bg-gray-700 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            Previous
          </button>

          {pageNumbers.map(number => (
            <button
              key={number}
              onClick={() => setCurrentPage(number)}
              className={
                currentPage === number
                ? 'px-2 py-1 text-sm border rounded bg-primary-600 text-white border-primary-600'
                : 'px-2 py-1 text-sm border rounded text-gray-700 dark:text-gray-300 border-gray-300 dark:border-gray-600 hover:bg-gray-50 dark:hover:bg-gray-700'
              }
            >
              {number}
            </button>
          ))}

          <button
            onClick={() => setCurrentPage(Math.min(totalPages, currentPage + 1))}
            disabled={currentPage === totalPages}
            className="px-2 py-1 text-sm border rounded text-gray-700 dark:text-gray-300 border-gray-300 dark:border-gray-600 
              hover:bg-gray-50 dark:hover:bg-gray-700 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            Next
          </button>

          <div className="ml-4">
            <select
              className="form-input text-sm"
              value={itemsPerPage}
              onChange={handleItemsPerPageChange}
            >
              <option value={5}>5 per page</option>
              <option value={10}>10 per page</option>
              <option value={20}>20 per page</option>
            </select>
          </div>
        </div>
      </div>
    );
  };

  // Loading state
  if (loading) {
    return (
      <div className="flex h-full min-h-[400px] items-center justify-center">
        <div className="h-12 w-12 animate-spin rounded-full border-4 border-primary-600 border-t-transparent"></div>
      </div>
    );
  }

  // Error state
  if (error) {
    return (
      <div className="bg-red-50 dark:bg-red-900/30 border border-red-400 text-red-700 dark:text-red-400 px-4 py-3 rounded mb-4" role="alert">
        <span className="block sm:inline">{error}</span>
      </div>
    );
  }

  return (
    <div>
      <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-8">Trackers</h1>

      {/* Success message */}
      {successMessage && (
        <div className="p-3 bg-success-100 dark:bg-success-900/30 border border-success-200 text-success-700 dark:text-success-300 rounded mb-4" role="alert">
          <span className="block sm:inline">{successMessage}</span>
        </div>
      )}

      {/* Search and Add controls */}
      <div className="mb-6 card p-4">
        <div className="flex flex-col gap-4 md:flex-row md:items-center md:justify-between">
          {/* Search input */}
          <div className="w-full md:w-1/3">
            <div className="relative">
              <input
                type="text"
                placeholder="Search trackers..."
                className="form-input pl-10"
                value={searchTerm}
                onChange={handleSearchChange}
              />
              <MagnifyingGlassIcon className="absolute left-3 top-2.5 h-5 w-5 text-gray-400" />
            </div>
          </div>

          {/* Add button - Admin only */}
          {isAdmin() && (
            <button 
              className="btn-primary"
              onClick={() => setShowAddModal(true)}
            >
              <PlusIcon className="h-4 w-4 mr-2" />
              Add New Tracker
            </button>
          )}
        </div>
      </div>

      {/* Trackers table */}
      <div className="table-container">
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200 dark:divide-gray-700">
            <thead className="bg-gray-50 dark:bg-gray-700">
              <tr>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                  Tool
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                  Descriptions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white dark:bg-gray-800 divide-y divide-gray-200 dark:divide-gray-700">
              {currentTools.map((tool) => (
                <React.Fragment key={tool.id}>
                  <tr 
                    className="hover:bg-gray-50 dark:hover:bg-gray-700 transition-colors duration-150 cursor-pointer"
                    onClick={() => toggleDropdown(tool.id)}
                  >
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex items-center">
                        <span className="text-sm font-medium text-gray-900 dark:text-white">{tool.name}</span>
                        {tool.isOpen ? (
                          <ChevronUpIcon className="h-4 w-4 ml-2 text-gray-500" />
                        ) : (
                          <ChevronDownIcon className="h-4 w-4 ml-2 text-gray-500" />
                        )}
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className="text-sm text-gray-500 dark:text-gray-300">
                        {tool.descriptions.length} {tool.descriptions.length === 1 ? 'item' : 'items'}
                      </span>
                    </td>
                  </tr>

                  {/* Dropdown rows */}
                  {tool.isOpen && tool.descriptions.map((desc) => (
                    <tr key={desc.id} className="bg-gray-50 dark:bg-gray-750">
                      <td className="px-6 py-3 whitespace-nowrap border-l-4 border-primary-500"></td>
                      <td className="px-6 py-3">
                        <div className="flex justify-between items-center">
                          {editMode.toolId === tool.id && editMode.descriptionId === desc.id ? (
                            <div className="flex-1 mr-2">
                              <input
                                type="text"
                                className="form-input w-full"
                                value={editText}
                                onChange={(e) => setEditText(e.target.value)}
                                autoFocus
                              />
                            </div>
                          ) : (
                            <span className="text-sm text-gray-900 dark:text-white">
                              {desc.description}
                            </span>
                          )}

                          {/* Edit controls - Admin only */}
                          {isAdmin() && (
                            <div className="flex space-x-2">
                              {editMode.toolId === tool.id && editMode.descriptionId === desc.id ? (
                                <>
                                  <button 
                                    onClick={() => handleEditSave(tool.id, desc.id)}
                                    className="text-green-600 hover:text-green-800 dark:text-green-400 dark:hover:text-green-300"
                                  >
                                    Save
                                  </button>
                                  <button 
                                    onClick={handleEditCancel}
                                    className="text-gray-600 hover:text-gray-800 dark:text-gray-400 dark:hover:text-gray-300"
                                  >
                                    Cancel
                                  </button>
                                </>
                              ) : (
                                <button 
                                  onClick={(e) => {
                                    e.stopPropagation();
                                    handleEditStart(tool.id, desc.id, desc.description);
                                  }}
                                  className="text-primary-600 hover:text-primary-900 dark:text-primary-400 dark:hover:text-primary-300"
                                >
                                  <PencilSquareIcon className="h-5 w-5" />
                                </button>
                              )}
                            </div>
                          )}
                        </div>
                      </td>
                    </tr>
                  ))}

                  {/* Add description button - Admin only */}
                  {isAdmin() && tool.isOpen && (
                    <tr className="bg-gray-50 dark:bg-gray-750">
                      <td className="px-6 py-2 whitespace-nowrap border-l-4 border-primary-500"></td>
                      <td className="px-6 py-2">
                        <button 
                          className="text-sm text-primary-600 hover:text-primary-800 dark:text-primary-400 dark:hover:text-primary-300 flex items-center"
                          onClick={(e) => {
                            e.stopPropagation();
                            setSelectedTool(tool.id);
                            setShowAddModal(true);
                          }}
                        >
                          <PlusIcon className="h-4 w-4 mr-1" />
                          Add new description
                        </button>
                      </td>
                    </tr>
                  )}
                </React.Fragment>
              ))}

              {filteredTrackers.length === 0 && (
                <tr>
                  <td colSpan={2} className="px-6 py-4 text-center text-gray-500 dark:text-gray-400">
                    No trackers found. {searchTerm && 'Try a different search term.'}
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>

        {/* Pagination */}
        {filteredTrackers.length > 0 && renderPagination()}
      </div>

      {/* Add Modal */}
      {showAddModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-lg max-w-md w-full">
            <h3 className="text-lg font-bold mb-4 text-gray-900 dark:text-white">
              {selectedTool ? 'Add New Description' : 'Add New Tool'}
            </h3>

            <div className="space-y-4">
              {selectedTool ? (
                <div>
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                    Description
                  </label>
                  <input
                    type="text"
                    className="form-input w-full"
                    value={newDescription}
                    onChange={(e) => setNewDescription(e.target.value)}
                    placeholder="Enter description"
                  />
                </div>
              ) : (
                <div>
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                    Tool Name
                  </label>
                  <input
                    type="text"
                    className="form-input w-full"
                    value={newToolName}
                    onChange={(e) => setNewToolName(e.target.value)}
                    placeholder="Enter tool name"
                  />
                </div>
              )}

              <div className="flex justify-end space-x-2 mt-6">
                <button
                  className="btn-ghost"
                  onClick={() => {
                    setShowAddModal(false);
                    setSelectedTool(null);
                    setNewToolName('');
                    setNewDescription('');
                  }}
                >
                  Cancel
                </button>

                <button
                  className="btn-primary"
                  onClick={selectedTool ? handleAddDescription : handleAddTool}
                >
                  Save
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Trackers;
