import React, { useState, useEffect } from 'react';
import { 
  ChevronDownIcon,
  ChevronUpIcon,
  MagnifyingGlassIcon,
  PencilSquareIcon,
  PlusIcon
} from '@heroicons/react/24/outline';
import { useAuth } from '../context/AuthContext';

// Define the tracker data structure
interface TrackerDescription {
  id: number;
  description: string;
  url?: string; // Optional URL if we want to link to external resources
  editedBy?: string;
  lastEdited?: Date;
}

interface TrackerTool {
  id: number;
  name: string;
  descriptions: TrackerDescription[];
  isOpen: boolean; // For dropdown state
}

const Trackers: React.FC = () => {
  const { isAdmin } = useAuth();
  const [trackers, setTrackers] = useState<TrackerTool[]>([]);
  const [filteredTrackers, setFilteredTrackers] = useState<TrackerTool[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(5);
  const [editMode, setEditMode] = useState({ toolId: -1, descriptionId: -1 });
  const [editText, setEditText] = useState('');
  const [showAddModal, setShowAddModal] = useState(false);
  const [newToolName, setNewToolName] = useState('');
  const [newDescription, setNewDescription] = useState('');
  const [selectedTool, setSelectedTool] = useState<number | null>(null);
  const [successMessage, setSuccessMessage] = useState<string | null>(null);

  // Initialize with data from the image
  useEffect(() => {
    // Simulating API call with the data from the image
    const fetchData = () => {
      setLoading(true);
      try {
        const data: TrackerTool[] = [
          {
            id: 1,
            name: 'Inventory Details',
            isOpen: false,
            descriptions: [
              { id: 1, description: 'LTF Application with Private IP details' },
              { id: 2, description: 'LTF Application with Public IP details & URLs' },
              { id: 3, description: 'Asset Details - Network Devices for all locations' },
              { id: 4, description: 'Asset Details - Servers for On Prem and GCP' },
              { id: 5, description: 'Endpoint Details ( Laptop & Desktop )' },
              { id: 6, description: 'Branch Location VLAN Segment Details' },
              { id: 7, description: 'Branch Location IP subnet Details' },
              { id: 8, description: 'Saas Based tool IP Details' }
            ]
          },
          {
            id: 2,
            name: 'Netskope',
            isOpen: false,
            descriptions: [
              { id: 9, description: 'Email Exception Sheet' },
              { id: 10, description: 'Whatsapp Access Sheet' },
              { id: 11, description: 'Generative AI Sheet' },
              { id: 12, description: 'Exception URL List' },
              { id: 13, description: 'Resigned User List' },
              { id: 14, description: 'BH User List' },
              { id: 15, description: 'Web Proxy Checklist' }
            ]
          },
          {
            id: 3,
            name: 'ZPA VPN',
            isOpen: false,
            descriptions: [
              { id: 16, description: 'ZPA Server or FQDN Access Sheet' },
              { id: 17, description: 'ZPA Groups Sheet' }
            ]
          }
        ];

        setTrackers(data);
        setFilteredTrackers(data);
        setError(null);
      } catch (err) {
        console.error('Error fetching trackers:', err);
        setError('Failed to load tracker data. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Return the component JSX
  return (
    <div>
      <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-8">Trackers</h1>
      <p>Tracker content will go here</p>
    </div>
  );
};

export default Trackers;
