import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { 
  MagnifyingGlassIcon,
  ArrowDownTrayIcon,
  ArrowUpTrayIcon,
  ChevronDownIcon,
  ChevronUpIcon,
  DocumentArrowDownIcon,
  PlusIcon,
  XMarkIcon
} from '@heroicons/react/24/outline';

interface Server {
  id: number;
  hostname: string;
  ipAddress: string;
  operatingSystem: string;
  serverRole: string;
  serverType: string;
  applicationName: string;
  applicationSPOC: string;
  applicationOwner: string;
  platform: string;
  location: string;
  manufacturer: string;
  ram: string;
  cpu: string;
  status: string;
  createdAt: string;
  updatedAt: string;
}

interface SortConfig {
  key: keyof Server;
  direction: 'asc' | 'desc';
}

// Mock data for development and testing
const MOCK_SERVERS: Server[] = [
  {
    id: 1,
    hostname: 'app-server-01',
    ipAddress: '192.168.1.10',
    operatingSystem: 'Ubuntu 20.04 LTS',
    serverRole: 'Application Server',
    serverType: 'Virtual Machine',
    applicationName: 'CRM System',
    applicationSPOC: 'John Smith',
    applicationOwner: 'Sales Department',
    platform: 'VMware',
    location: 'US East',
    manufacturer: 'Dell',
    ram: '32GB',
    cpu: 'Intel Xeon 8-core',
    status: 'live',
    createdAt: new Date().toISOString(),
    updatedAt: new Date().toISOString()
  },
  {
    id: 2,
    hostname: 'db-server-01',
    ipAddress: '192.168.1.11',
    operatingSystem: 'Red Hat Enterprise Linux 8',
    serverRole: 'Database Server',
    serverType: 'Physical',
    applicationName: 'Customer Database',
    applicationSPOC: 'Jane Doe',
    applicationOwner: 'IT Department',
    platform: 'Bare Metal',
    location: 'US West',
    manufacturer: 'HP',
    ram: '64GB',
    cpu: 'AMD EPYC 16-core',
    status: 'live',
    createdAt: new Date().toISOString(),
    updatedAt: new Date().toISOString()
  },
  {
    id: 3,
    hostname: 'web-server-01',
    ipAddress: '192.168.1.12',
    operatingSystem: 'Windows Server 2019',
    serverRole: 'Web Server',
    serverType: 'Virtual Machine',
    applicationName: 'Corporate Website',
    applicationSPOC: 'Alex Johnson',
    applicationOwner: 'Marketing Department',
    platform: 'Hyper-V',
    location: 'EU Central',
    manufacturer: 'Cisco',
    ram: '16GB',
    cpu: 'Intel Xeon 4-core',
    status: 'shutdown',
    createdAt: new Date().toISOString(),
    updatedAt: new Date().toISOString()
  },
  {
    id: 4,
    hostname: 'mail-server-01',
    ipAddress: '192.168.1.13',
    operatingSystem: 'Ubuntu 22.04 LTS',
    serverRole: 'Mail Server',
    serverType: 'Virtual Machine',
    applicationName: 'Corporate Email',
    applicationSPOC: 'Maria Garcia',
    applicationOwner: 'IT Department',
    platform: 'AWS EC2',
    location: 'US East',
    manufacturer: 'Amazon',
    ram: '8GB',
    cpu: 'Intel Xeon 2-core',
    status: 'live',
    createdAt: new Date().toISOString(),
    updatedAt: new Date().toISOString()
  },
  {
    id: 5,
    hostname: 'test-server-01',
    ipAddress: '192.168.1.14',
    operatingSystem: 'CentOS 7',
    serverRole: 'Test Server',
    serverType: 'Virtual Machine',
    applicationName: 'QA Testing',
    applicationSPOC: 'Robert Lee',
    applicationOwner: 'QA Department',
    platform: 'VMware',
    location: 'US West',
    manufacturer: 'Dell',
    ram: '16GB',
    cpu: 'Intel Xeon 4-core',
    status: 'new',
    createdAt: new Date().toISOString(),
    updatedAt: new Date().toISOString()
  }
];

const ServerInventory: React.FC = () => {
  const [servers, setServers] = useState<Server[]>([]);
  const [filteredServers, setFilteredServers] = useState<Server[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortConfig, setSortConfig] = useState<SortConfig>({ key: 'hostname', direction: 'asc' });
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(10);
  const [fileUploadError, setFileUploadError] = useState<string | null>(null);
  const [fileUploadSuccess, setFileUploadSuccess] = useState<string | null>(null);
  const [filters, setFilters] = useState({
    operatingSystem: '',
    location: '',
    applicationOwner: '',
    status: ''
  });

  // Options for filters derived from data
  const [filterOptions, setFilterOptions] = useState({
    operatingSystems: [] as string[],
    locations: [] as string[],
    applicationOwners: [] as string[],
    statuses: ['live', 'shutdown', 'new']
  });

  const [showAddModal, setShowAddModal] = useState(false);
  const [newServerData, setNewServerData] = useState<Partial<Server>>({
    hostname: '',
    ipAddress: '',
    operatingSystem: '',
    serverRole: '',
    serverType: '',
    applicationName: '',
    applicationSPOC: '',
    applicationOwner: '',
    platform: '',
    location: '',
    manufacturer: '',
    ram: '',
    cpu: '',
    status: 'new'
  });

  useEffect(() => {
    const fetchServers = async () => {
      try {
        setLoading(true);
        
        // Comment this block to use mock data
        /* 
        const response = await axios.get<Server[]>('/api/servers');
        const serverData = Array.isArray(response.data) ? response.data : [];
        */
        
        // Use mock data instead of API call for now
        const serverData = MOCK_SERVERS;
        
        setServers(serverData);
        setFilteredServers(serverData);
        extractFilterOptions(serverData);
        setError(null);
      } catch (err) {
        console.error('Error fetching servers:', err);
        setError('Failed to load servers. Please try again later.');
        setServers([]);
        setFilteredServers([]);
      } finally {
        setLoading(false);
      }
    };

    fetchServers();
  }, []);

  const extractFilterOptions = (serverData: Server[]) => {
    const operatingSystems = [...new Set(serverData.map(server => server.operatingSystem))];
    const locations = [...new Set(serverData.map(server => server.location))];
    const applicationOwners = [...new Set(serverData.map(server => server.applicationOwner))];
    
    setFilterOptions({
      operatingSystems,
      locations,
      applicationOwners,
      statuses: ['live', 'shutdown', 'new']
    });
  };

  // Apply search and filters to the servers
  useEffect(() => {
    if (!Array.isArray(servers)) {
      setFilteredServers([]);
      return;
    }
    
    const filtered = servers.filter(server => {
      // Search term check across multiple fields
      const searchCheck = searchTerm === '' || 
        Object.values(server).some(
          value => value && value.toString().toLowerCase().includes(searchTerm.toLowerCase())
        );
      
      // Filter checks
      const osCheck = filters.operatingSystem === '' || server.operatingSystem === filters.operatingSystem;
      const locationCheck = filters.location === '' || server.location === filters.location;
      const ownerCheck = filters.applicationOwner === '' || server.applicationOwner === filters.applicationOwner;
      const statusCheck = filters.status === '' || server.status === filters.status;
      
      return searchCheck && osCheck && locationCheck && ownerCheck && statusCheck;
    });
    
    // Apply sorting
    const sortedData = [...filtered].sort((a, b) => {
      if (a[sortConfig.key] < b[sortConfig.key]) {
        return sortConfig.direction === 'asc' ? -1 : 1;
      }
      if (a[sortConfig.key] > b[sortConfig.key]) {
        return sortConfig.direction === 'asc' ? 1 : -1;
      }
      return 0;
    });

    setFilteredServers(sortedData);
    setCurrentPage(1); // Reset to first page whenever filters change
  }, [searchTerm, filters, sortConfig, servers]);

  // Handle sort click
  const handleSort = (key: keyof Server) => {
    const direction = sortConfig.key === key && sortConfig.direction === 'asc' ? 'desc' : 'asc';
    setSortConfig({ key, direction });
  };

  // Get sort icon
  const getSortIcon = (columnName: keyof Server) => {
    if (sortConfig.key !== columnName) {
      return null;
    }
    return sortConfig.direction === 'asc' ? 
      <ChevronUpIcon className="h-4 w-4 ml-1" /> : 
      <ChevronDownIcon className="h-4 w-4 ml-1" />;
  };

  // Filter change handlers
  const handleFilterChange = (filter: keyof typeof filters, value: string) => {
    setFilters(prev => ({ ...prev, [filter]: value }));
  };

  // Reset filters
  const resetFilters = () => {
    setSearchTerm('');
    setFilters({
      operatingSystem: '',
      location: '',
      applicationOwner: '',
      status: ''
    });
  };

  // Pagination calculations
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = Array.isArray(filteredServers) 
    ? filteredServers.slice(indexOfFirstItem, indexOfLastItem) 
    : [];
  const totalPages = Math.ceil((Array.isArray(filteredServers) ? filteredServers.length : 0) / itemsPerPage);

  // Change page handler
  const paginate = (pageNumber: number) => setCurrentPage(pageNumber);

  // Handle items per page change
  const handleItemsPerPageChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
    setItemsPerPage(parseInt(event.target.value));
    setCurrentPage(1); // Reset to first page
  };

  // Download template handler
  const handleDownloadTemplate = () => {
    const link = document.createElement('a');
    link.href = '/api/servers/template';
    link.setAttribute('download', 'server_template.xlsx');
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  // Export data handler
  const handleExport = () => {
    const exportData = filteredServers.length > 0 ? filteredServers : servers;
    
    // Prepare data for export
    const csvContent = [
      Object.keys(exportData[0]).join(','), // Header row
      ...exportData.map(server => Object.values(server).join(',')) // Data rows
    ].join('\n');
    
    // Create and trigger download
    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.setAttribute('href', url);
    link.setAttribute('download', 'server_inventory.csv');
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  // File upload handler
  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    
    if (!file) {
      return;
    }
    
    const formData = new FormData();
    formData.append('file', file);
    
    try {
      setFileUploadError(null);
      setFileUploadSuccess(null);
      
      const response = await axios.post('/api/servers/import', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      
      setFileUploadSuccess('File uploaded successfully. ' + response.data.message);
      
      // Refresh server list
      const serversResponse = await axios.get<Server[]>('/api/servers');
      setServers(serversResponse.data);
      extractFilterOptions(serversResponse.data);
      
    } catch (err: any) {
      setFileUploadError('File upload failed. ' + (err.response?.data?.message || err.message));
    }
    
    // Clear the input field to allow uploading the same file again
    event.target.value = '';
  };

  // New function to handle form input changes
  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setNewServerData(prev => ({ ...prev, [name]: value }));
  };

  // New function to handle server creation
  const handleAddServer = (e: React.FormEvent) => {
    e.preventDefault();
    
    // Create new server object
    const newServer: Server = {
      id: Math.max(...(Array.isArray(servers) ? servers.map(s => s.id) : [0])) + 1,
      ...newServerData as any,
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString()
    };
    
    // Add to servers array
    const updatedServers = [...(Array.isArray(servers) ? servers : []), newServer];
    setServers(updatedServers);
    
    // Close modal and reset form
    setShowAddModal(false);
    setNewServerData({
      hostname: '',
      ipAddress: '',
      operatingSystem: '',
      serverRole: '',
      serverType: '',
      applicationName: '',
      applicationSPOC: '',
      applicationOwner: '',
      platform: '',
      location: '',
      manufacturer: '',
      ram: '',
      cpu: '',
      status: 'new'
    });
    
    // Show success message
    setFileUploadSuccess('Server added successfully');
    setTimeout(() => setFileUploadSuccess(null), 3000);
  };

  if (loading) {
    return (
      <div className="flex h-full min-h-[400px] items-center justify-center">
        <div className="h-12 w-12 animate-spin rounded-full border-4 border-primary-600 border-t-transparent"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 dark:bg-red-900/30 border border-red-400 text-red-700 dark:text-red-400 px-4 py-3 rounded mb-4" role="alert">
        <span className="block sm:inline">{error}</span>
      </div>
    );
  }

  return (
    <div>
      <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-8">Server Inventory</h1>
      
      {/* Search and filter controls */}
      <div className="mb-6 bg-white dark:bg-gray-800 p-4 rounded-lg shadow">
        <div className="flex flex-col gap-4 md:flex-row md:items-center md:justify-between">
          {/* Search input */}
          <div className="w-full md:w-1/3">
            <div className="relative">
              <input
                type="text"
                placeholder="Search servers..."
                className="pl-10 pr-4 py-2 w-full border border-gray-300 dark:border-gray-700 rounded-md dark:bg-gray-700 dark:text-white"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
              <MagnifyingGlassIcon className="absolute left-3 top-2.5 h-5 w-5 text-gray-400" />
            </div>
          </div>
          
          <div className="flex flex-wrap gap-2">
            {/* Add Server button */}
            <button 
              className="inline-flex items-center px-4 py-2 bg-green-600 border border-transparent rounded-md font-semibold text-xs text-white tracking-widest hover:bg-green-700"
              onClick={() => setShowAddModal(true)}
            >
              <PlusIcon className="h-4 w-4 mr-2" />
              Add Server
            </button>
            
            {/* Import/Export buttons */}
            <div className="flex space-x-2">
              <label className="inline-flex items-center px-4 py-2 bg-primary-600 border border-transparent rounded-md font-semibold text-xs text-white tracking-widest hover:bg-primary-700 cursor-pointer">
                <ArrowUpTrayIcon className="h-4 w-4 mr-2" />
                Import
                <input type="file" className="hidden" onChange={handleFileUpload} accept=".xlsx,.xls,.csv" />
              </label>
              
              <button 
                className="inline-flex items-center px-4 py-2 bg-primary-600 border border-transparent rounded-md font-semibold text-xs text-white tracking-widest hover:bg-primary-700"
                onClick={handleExport}
              >
                <ArrowDownTrayIcon className="h-4 w-4 mr-2" />
                Export
              </button>
              
              <button 
                className="inline-flex items-center px-4 py-2 bg-gray-600 border border-transparent rounded-md font-semibold text-xs text-white tracking-widest hover:bg-gray-700"
                onClick={handleDownloadTemplate}
              >
                <DocumentArrowDownIcon className="h-4 w-4 mr-2" />
                Template
              </button>
            </div>
          </div>
        </div>
        
        {/* Filter dropdowns */}
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-4 mt-4">
          <select 
            className="border border-gray-300 dark:border-gray-700 rounded-md p-2 dark:bg-gray-700 dark:text-white"
            value={filters.operatingSystem}
            onChange={(e) => handleFilterChange('operatingSystem', e.target.value)}
          >
            <option value="">All Operating Systems</option>
            {filterOptions.operatingSystems.map((os) => (
              <option key={os} value={os}>{os}</option>
            ))}
          </select>
          
          <select 
            className="border border-gray-300 dark:border-gray-700 rounded-md p-2 dark:bg-gray-700 dark:text-white"
            value={filters.location}
            onChange={(e) => handleFilterChange('location', e.target.value)}
          >
            <option value="">All Locations</option>
            {filterOptions.locations.map((location) => (
              <option key={location} value={location}>{location}</option>
            ))}
          </select>
          
          <select 
            className="border border-gray-300 dark:border-gray-700 rounded-md p-2 dark:bg-gray-700 dark:text-white"
            value={filters.applicationOwner}
            onChange={(e) => handleFilterChange('applicationOwner', e.target.value)}
          >
            <option value="">All Application Owners</option>
            {filterOptions.applicationOwners.map((owner) => (
              <option key={owner} value={owner}>{owner}</option>
            ))}
          </select>
          
          <select 
            className="border border-gray-300 dark:border-gray-700 rounded-md p-2 dark:bg-gray-700 dark:text-white"
            value={filters.status}
            onChange={(e) => handleFilterChange('status', e.target.value)}
          >
            <option value="">All Statuses</option>
            {filterOptions.statuses.map((status) => (
              <option key={status} value={status}>{status}</option>
            ))}
          </select>
        </div>
        
        {/* Reset filters button */}
        <div className="mt-4 flex justify-end">
          <button 
            className="px-4 py-2 bg-gray-200 dark:bg-gray-700 border border-transparent rounded-md font-semibold text-xs text-gray-700 dark:text-white tracking-widest hover:bg-gray-300 dark:hover:bg-gray-600"
            onClick={resetFilters}
          >
            Reset Filters
          </button>
        </div>

        {/* Upload status messages */}
        {fileUploadError && (
          <div className="mt-4 p-3 bg-red-100 dark:bg-red-900/30 border border-red-400 text-red-700 dark:text-red-400 rounded">
            {fileUploadError}
          </div>
        )}
        
        {fileUploadSuccess && (
          <div className="mt-4 p-3 bg-green-100 dark:bg-green-900/30 border border-green-400 text-green-700 dark:text-green-400 rounded">
            {fileUploadSuccess}
          </div>
        )}
      </div>
      
      {/* Server table */}
      <div className="bg-white dark:bg-gray-800 shadow overflow-hidden sm:rounded-lg">
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200 dark:divide-gray-700">
            <thead className="bg-gray-50 dark:bg-gray-700">
              <tr>
                <th 
                  scope="col" 
                  className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider cursor-pointer"
                  onClick={() => handleSort('hostname')}
                >
                  <div className="flex items-center">
                    Hostname
                    {getSortIcon('hostname')}
                  </div>
                </th>
                <th 
                  scope="col" 
                  className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider cursor-pointer"
                  onClick={() => handleSort('ipAddress')}
                >
                  <div className="flex items-center">
                    IP Address
                    {getSortIcon('ipAddress')}
                  </div>
                </th>
                <th 
                  scope="col" 
                  className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider cursor-pointer"
                  onClick={() => handleSort('operatingSystem')}
                >
                  <div className="flex items-center">
                    OS
                    {getSortIcon('operatingSystem')}
                  </div>
                </th>
                <th 
                  scope="col" 
                  className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider cursor-pointer"
                  onClick={() => handleSort('serverRole')}
                >
                  <div className="flex items-center">
                    Role
                    {getSortIcon('serverRole')}
                  </div>
                </th>
                <th 
                  scope="col" 
                  className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider cursor-pointer"
                  onClick={() => handleSort('applicationName')}
                >
                  <div className="flex items-center">
                    Application
                    {getSortIcon('applicationName')}
                  </div>
                </th>
                <th 
                  scope="col" 
                  className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider cursor-pointer"
                  onClick={() => handleSort('location')}
                >
                  <div className="flex items-center">
                    Location
                    {getSortIcon('location')}
                  </div>
                </th>
                <th 
                  scope="col" 
                  className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider cursor-pointer"
                  onClick={() => handleSort('status')}
                >
                  <div className="flex items-center">
                    Status
                    {getSortIcon('status')}
                  </div>
                </th>
              </tr>
            </thead>
            <tbody className="bg-white dark:bg-gray-800 divide-y divide-gray-200 dark:divide-gray-700">
              {currentItems.length > 0 ? (
                currentItems.map((server) => (
                  <tr key={server.id}>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900 dark:text-white">{server.hostname}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-300">{server.ipAddress}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-300">{server.operatingSystem}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-300">{server.serverRole}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-300">{server.applicationName}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-300">{server.location}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm">
                      <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full 
                        ${server.status === 'live' ? 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200' : 
                          server.status === 'shutdown' ? 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200' : 
                          'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200'}`}>
                        {server.status}
                      </span>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan={7} className="px-6 py-4 text-center text-sm text-gray-500 dark:text-gray-300">
                    No servers found
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
        
        {/* Pagination */}
        <div className="px-6 py-3 flex items-center justify-between border-t border-gray-200 dark:border-gray-700 bg-gray-50 dark:bg-gray-700">
          <div className="flex-1 flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-700 dark:text-gray-300">
                Showing <span className="font-medium">{indexOfFirstItem + 1}</span> to{' '}
                <span className="font-medium">
                  {Math.min(indexOfLastItem, filteredServers.length)}
                </span>{' '}
                of <span className="font-medium">{filteredServers.length}</span> results
              </p>
            </div>
            <div className="flex items-center space-x-4">
              <select
                className="border border-gray-300 dark:border-gray-600 rounded-md p-2 dark:bg-gray-800 dark:text-white"
                value={itemsPerPage}
                onChange={handleItemsPerPageChange}
              >
                <option value={5}>5 per page</option>
                <option value={10}>10 per page</option>
                <option value={25}>25 per page</option>
                <option value={50}>50 per page</option>
              </select>
              <nav className="relative z-0 inline-flex rounded-md shadow-sm -space-x-px" aria-label="Pagination">
                <button
                  onClick={() => paginate(Math.max(1, currentPage - 1))}
                  className="relative inline-flex items-center px-2 py-2 rounded-l-md border border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-800 text-sm font-medium text-gray-500 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700"
                  disabled={currentPage === 1}
                >
                  <span className="sr-only">Previous</span>
                  &larr;
                </button>
                {Array.from({ length: Math.min(5, totalPages) }).map((_, idx) => {
                  const pageToShow = currentPage <= 3 
                    ? idx + 1 
                    : currentPage >= totalPages - 2 
                      ? totalPages - 4 + idx 
                      : currentPage - 2 + idx;
                  
                  if (pageToShow > 0 && pageToShow <= totalPages) {
                    return (
                      <button
                        key={pageToShow}
                        onClick={() => paginate(pageToShow)}
                        className={`relative inline-flex items-center px-4 py-2 border ${
                          currentPage === pageToShow
                            ? 'bg-primary-600 text-white z-10 border-primary-600'
                            : 'bg-white dark:bg-gray-800 text-gray-700 dark:text-gray-300 border-gray-300 dark:border-gray-600 hover:bg-gray-50 dark:hover:bg-gray-700'
                        }`}
                      >
                        {pageToShow}
                      </button>
                    );
                  }
                  return null;
                })}
                <button
                  onClick={() => paginate(Math.min(totalPages, currentPage + 1))}
                  className="relative inline-flex items-center px-2 py-2 rounded-r-md border border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-800 text-sm font-medium text-gray-500 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700"
                  disabled={currentPage === totalPages}
                >
                  <span className="sr-only">Next</span>
                  &rarr;
                </button>
              </nav>
            </div>
          </div>
        </div>
      </div>

      {/* Add Server Modal */}
      {showAddModal && (
        <div className="fixed inset-0 z-50 overflow-y-auto">
          <div className="flex items-end justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
            <div className="fixed inset-0 transition-opacity" aria-hidden="true">
              <div className="absolute inset-0 bg-gray-500 opacity-75"></div>
            </div>
            <span className="hidden sm:inline-block sm:align-middle sm:h-screen" aria-hidden="true">&#8203;</span>
            <div className="inline-block align-bottom bg-white dark:bg-gray-800 rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full">
              <div className="bg-white dark:bg-gray-800 px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
                <div className="sm:flex sm:items-start">
                  <div className="mt-3 text-center sm:mt-0 sm:ml-4 sm:text-left w-full">
                    <h3 className="text-lg leading-6 font-medium text-gray-900 dark:text-white flex justify-between">
                      Add New Server
                      <button onClick={() => setShowAddModal(false)} className="text-gray-400 hover:text-gray-500">
                        <XMarkIcon className="h-6 w-6" />
                      </button>
                    </h3>
                    <div className="mt-4">
                      <form onSubmit={handleAddServer} className="space-y-4">
                        {/* Basic Info */}
                        <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
                          <div>
                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                              Hostname*
                            </label>
                            <input
                              type="text"
                              name="hostname"
                              required
                              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md dark:bg-gray-700 dark:text-white"
                              value={newServerData.hostname}
                              onChange={handleInputChange}
                            />
                          </div>
                          <div>
                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                              IP Address*
                            </label>
                            <input
                              type="text"
                              name="ipAddress"
                              required
                              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md dark:bg-gray-700 dark:text-white"
                              value={newServerData.ipAddress}
                              onChange={handleInputChange}
                            />
                          </div>
                        </div>
                        
                        {/* Server Details */}
                        <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
                          <div>
                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                              Operating System*
                            </label>
                            <input
                              type="text"
                              name="operatingSystem"
                              required
                              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md dark:bg-gray-700 dark:text-white"
                              value={newServerData.operatingSystem}
                              onChange={handleInputChange}
                            />
                          </div>
                          <div>
                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                              Server Role*
                            </label>
                            <input
                              type="text"
                              name="serverRole"
                              required
                              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md dark:bg-gray-700 dark:text-white"
                              value={newServerData.serverRole}
                              onChange={handleInputChange}
                            />
                          </div>
                        </div>
                        
                        <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
                          <div>
                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                              Server Type*
                            </label>
                            <input
                              type="text"
                              name="serverType"
                              required
                              placeholder="Physical, Virtual, Container, etc."
                              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md dark:bg-gray-700 dark:text-white"
                              value={newServerData.serverType}
                              onChange={handleInputChange}
                            />
                          </div>
                          <div>
                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                              Platform
                            </label>
                            <input
                              type="text"
                              name="platform"
                              placeholder="VMware, AWS, Bare Metal, etc."
                              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md dark:bg-gray-700 dark:text-white"
                              value={newServerData.platform}
                              onChange={handleInputChange}
                            />
                          </div>
                        </div>
                        
                        {/* Application Info */}
                        <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
                          <div>
                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                              Application Name*
                            </label>
                            <input
                              type="text"
                              name="applicationName"
                              required
                              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md dark:bg-gray-700 dark:text-white"
                              value={newServerData.applicationName}
                              onChange={handleInputChange}
                            />
                          </div>
                          <div>
                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                              Application SPOC
                            </label>
                            <input
                              type="text"
                              name="applicationSPOC"
                              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md dark:bg-gray-700 dark:text-white"
                              value={newServerData.applicationSPOC}
                              onChange={handleInputChange}
                            />
                          </div>
                        </div>
                        
                        <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
                          <div>
                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                              Application Owner*
                            </label>
                            <input
                              type="text"
                              name="applicationOwner"
                              required
                              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md dark:bg-gray-700 dark:text-white"
                              value={newServerData.applicationOwner}
                              onChange={handleInputChange}
                            />
                          </div>
                          <div>
                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                              Location*
                            </label>
                            <input
                              type="text"
                              name="location"
                              required
                              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md dark:bg-gray-700 dark:text-white"
                              value={newServerData.location}
                              onChange={handleInputChange}
                            />
                          </div>
                        </div>
                        
                        {/* Hardware Info */}
                        <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
                          <div>
                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                              Manufacturer
                            </label>
                            <input
                              type="text"
                              name="manufacturer"
                              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md dark:bg-gray-700 dark:text-white"
                              value={newServerData.manufacturer}
                              onChange={handleInputChange}
                            />
                          </div>
                          <div>
                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                              RAM
                            </label>
                            <input
                              type="text"
                              name="ram"
                              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md dark:bg-gray-700 dark:text-white"
                              value={newServerData.ram}
                              onChange={handleInputChange}
                            />
                          </div>
                        </div>
                        
                        <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
                          <div>
                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                              CPU
                            </label>
                            <input
                              type="text"
                              name="cpu"
                              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md dark:bg-gray-700 dark:text-white"
                              value={newServerData.cpu}
                              onChange={handleInputChange}
                            />
                          </div>
                          <div>
                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                              Status*
                            </label>
                            <select
                              name="status"
                              required
                              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md dark:bg-gray-700 dark:text-white"
                              value={newServerData.status}
                              onChange={handleInputChange}
                            >
                              <option value="live">Live</option>
                              <option value="shutdown">Shutdown</option>
                              <option value="new">New</option>
                            </select>
                          </div>
                        </div>
                        
                        <div className="mt-5 sm:mt-4 sm:flex sm:flex-row-reverse">
                          <button
                            type="submit"
                            className="w-full inline-flex justify-center rounded-md border border-transparent shadow-sm px-4 py-2 bg-green-600 text-base font-medium text-white hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500 sm:ml-3 sm:w-auto sm:text-sm"
                          >
                            Add Server
                          </button>
                          <button
                            type="button"
                            onClick={() => setShowAddModal(false)}
                            className="mt-3 w-full inline-flex justify-center rounded-md border border-gray-300 dark:border-gray-600 shadow-sm px-4 py-2 bg-white dark:bg-gray-700 text-base font-medium text-gray-700 dark:text-gray-200 hover:bg-gray-50 dark:hover:bg-gray-600 focus:outline-none sm:mt-0 sm:w-auto sm:text-sm"
                          >
                            Cancel
                          </button>
                        </div>
                      </form>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ServerInventory;
