sourcesContent:
import React, { useState, useEffect, useRef } from 'react';
import { 
  MagnifyingGlassIcon,
  ArrowDownTrayIcon,
  ArrowUpTrayIcon,
  ChevronDownIcon,
  ChevronUpIcon,
  DocumentArrowDownIcon,
  PlusIcon,
  XMarkIcon,
  TrashIcon,
  FunnelIcon
} from '@heroicons/react/24/outline';
import { serverApi } from '../services/api.js';
import { useAuth } from '../context/AuthContext.tsx';

interface Server {
  id: number;
  hostname: string;
  ipAddress?: string; // Backend uses ip
  ip?: string;        // Add for compatibility with backend
  operatingSystem: string;
  serverRole: string;
  serverType: string;
  applicationName: string;
  applicationSPOC: string;
  applicationOwner: string;
  platform: string;
  location: string;
  manufacturer: string;
  ram: string;
  cpu: string;
  status: string;
  createdAt: string;
  updatedAt: string;
}

interface ApiResponse {
  totalItems: number;
  servers: Server[];
  totalPages: number;
  currentPage: number;
}

interface SortConfig {
  key: keyof Server;
  direction: 'asc' | 'desc';
}

// Type for column width state
interface ColumnWidths {
  [key: string]: number;
}

// Default column widths
const defaultColumnWidths: ColumnWidths = {
  hostname: 150,
  ip: 120,
  operatingSystem: 180,
  serverRole: 120,
  serverType: 120,
  applicationName: 150,
  applicationSPOC: 150,
  applicationOwner: 150,
  location: 120,
  manufacturer: 120,
  ram: 80,
  cpu: 120,
  status: 100
};

const ServerInventory: React.FC = () => {
  const { isAdmin } = useAuth();
  const [servers, setServers] = useState<Server[]>([]);
  const [filteredServers, setFilteredServers] = useState<Server[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortConfig, setSortConfig] = useState<SortConfig>({ key: 'hostname', direction: 'asc' });
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(10);
  const [totalItems, setTotalItems] = useState(0);
  const [totalPages, setTotalPages] = useState(1);
  const [fileUploadError, setFileUploadError] = useState<string | null>(null);
  const [fileUploadSuccess, setFileUploadSuccess] = useState<string | null>(null);
  const [filters, setFilters] = useState({
    operatingSystem: '',
    location: '',
    applicationOwner: '',
    status: ''
  });

  const [allServers, setAllServers] = useState<Server[]>([]);
  const [queryParams, setQueryParams] = useState({
    page: 1,
    size: 10,
    search: '',
    operatingSystem: '',
    location: '',
    applicationOwner: '',
    status: '',
    sort: 'hostname',
    order: 'asc'
  });

  const [showAddModal, setShowAddModal] = useState(false);
  const [newServerData, setNewServerData] = useState<Partial<Server>>({
    
    hostname: '',
    ipAddress: '',
    operatingSystem: '',
    serverRole: '',
    serverType: '',
    applicationName: '',
    applicationSPOC: '',
    applicationOwner: '',
    platform: '',
    location: '',
    manufacturer: '',
    ram: '',
    cpu: '',
    status: 'new'
  });

  const [showClearAllConfirmation, setShowClearAllConfirmation] = useState(false);

  // Column widths state
  const [columnWidths, setColumnWidths] = useState<ColumnWidths>(() => {
    // Try to load stored column widths from localStorage
    const storedWidths = localStorage.getItem('serverTableColumnWidths');
    return storedWidths ? JSON.parse(storedWidths) : defaultColumnWidths;
  });
  
  // Refs for column resizing
  const resizingColumn = useRef<string | null>(null);
  const startX = useRef<number>(0);
  const startWidth = useRef<number>(0);
  const tableRef = useRef<HTMLTableElement>(null);
  
  // Save column widths to localStorage when they change
  useEffect(() => {
    localStorage.setItem('serverTableColumnWidths', JSON.stringify(columnWidths));
  }, [columnWidths]);

  useEffect(() => {
    // Initial fetch of all servers for filter options
    fetchAllServers();
    
    // Initial fetch of paginated data
    fetchServers(currentPage, itemsPerPage, queryParams.sort, queryParams.order, filters);
  }, []);

  // Only fetch when query params change
  useEffect(() => {
    fetchServers(currentPage, itemsPerPage, queryParams.sort, queryParams.order, filters);
  }, [currentPage, itemsPerPage, queryParams.sort, queryParams.order, filters]);

  // Fetch all servers for filter options
  const fetchAllServers = async () => {
    try {
      const response = await serverApi.getServers({ size: 1000 });
      const mappedServers = response.data.servers.map((server: any) => ({
        ...server,
        ipAddress: server.ip || server.ipAddress
      }));
      setAllServers(mappedServers);
    } catch (err) {
      console.error('Error fetching all servers for filters:', err);
    }
  };

  // Debug function to check API calls
  const logApiCall = (action: string, params: any) => {
    console.log(`${action} with params:`, params);
  };

  // Update fetch servers to synchronize sorting state with API response
  const fetchServers = async (
    currentPage: number, 
    limit: number,
    sortField?: keyof Server, 
    sortDirection?: 'asc' | 'desc',
    filters?: ServerFilters
  ) => {
    setLoading(true);
    try {
      // Build query parameters
      const params = new URLSearchParams();
      params.append('page', currentPage.toString());
      params.append('limit', limit.toString());
      
      if (sortField) {
        params.append('sortBy', sortField);
        params.append('sortOrder', sortDirection || 'asc');
      }
      
      // Add filters if they exist
      if (filters) {
        Object.entries(filters).forEach(([key, value]) => {
          if (value && value !== 'All') {
            params.append(key, value);
          }
        });
      }
      
      // Make the API call
      const response = await serverApi.getServers(params);
      
      if (response.data) {
        setServers(response.data.servers);
        setTotalPages(response.data.totalPages);
        setTotalItems(response.data.totalCount);
        
        // Update filter options with available values from data
        const uniqueValues = extractUniqueValues(response.data.servers);
        setFilterOptions(uniqueValues);
      }
    } catch (error) {
      console.error('Error fetching servers:', error);
      setError('Failed to load server data. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  // Get filter options based on currently selected filters - improved for all dropdowns
  const getFilterOptions = () => {
    // Start with all servers
    let filteredServers = [...allServers];
    
    // Apply current filters to get the filtered set of servers
    const filters_map: {[key: string]: string} = {
      operatingSystem: filters.operatingSystem,
      location: filters.location, 
      applicationOwner: filters.applicationOwner,
      status: filters.status
    };

    // Helper function to filter servers based on current selections except the target field
    const getFilteredServersExcept = (exceptField: string) => {
      return allServers.filter(server => {
        return Object.entries(filters_map)
          .filter(([field]) => field !== exceptField && filters_map[field] !== '')
          .every(([field, value]) => {
            // Ensure the server[field] exists and matches the filter value
            return server[field as keyof Server] === value;
          });
      });
    };

    // Get unique values for each filter, filtered by other selections
    const operatingSystems = [...new Set(
      getFilteredServersExcept('operatingSystem')
        .map(server => server.operatingSystem)
        .filter(Boolean)
    )].sort();
    
    const locations = [...new Set(
      getFilteredServersExcept('location')
        .map(server => server.location)
        .filter(Boolean)
    )].sort();
    
    const applicationOwners = [...new Set(
      getFilteredServersExcept('applicationOwner')
        .map(server => server.applicationOwner)
        .filter(Boolean)
    )].sort();

    // For statuses, filter based on all other selections
    const filteredForStatus = getFilteredServersExcept('status');
    const availableStatuses = [...new Set(
      filteredForStatus
        .map(server => server.status)
        .filter(Boolean)
    )];

    // Ensure all possible statuses are included even if not in filtered servers
    const statuses = ['live', 'shutdown', 'new'].filter(status => 
      availableStatuses.length === 0 || availableStatuses.includes(status)
    );
    
    return {
      operatingSystems,
      locations,
      applicationOwners,
      statuses
    };
  };

  // Improved handleSort to properly update sorting state and refresh data
  const handleSort = (key: keyof Server) => {
    // Determine new sort direction
    const newDirection = sortConfig.key === key && sortConfig.direction === 'asc' ? 'desc' : 'asc';
    
    // Update sort config
    setSortConfig({ key, direction: newDirection });
    
    // Update query params
    setQueryParams(prev => {
      const newParams = {
        ...prev,
        sort: key,
        order: newDirection
      };
      
      // Log the updated params
      logApiCall('Updating sort', newParams);
      
      return newParams;
    });
  };

  // Get sort icon
  const getSortIcon = (columnName: keyof Server) => {
    if (sortConfig.key !== columnName) {
      return null;
    }
    return sortConfig.direction === 'asc' ? 
      <ChevronUpIcon className="h-4 w-4 ml-1" /> : 
      <ChevronDownIcon className="h-4 w-4 ml-1" />;
  };

  // Handle filter change
  const handleFilterChange = (filter: keyof typeof filters, value: string) => {
    // Update local filter state
    setFilters(prev => {
      const newFilters = { ...prev, [filter]: value };
      
      // Update query params immediately - this will trigger a fetch via useEffect
      setQueryParams(prev => ({
        ...prev,
        page: 1, // Reset to page 1 when filter changes
        [filter]: value
      }));
      
      return newFilters;
    });
  };

  // Update search handling to also apply immediately
  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    setSearchTerm(value);
    
    // Debounce search to avoid too many API calls
    const timeoutId = setTimeout(() => {
      setQueryParams(prev => ({
        ...prev,
        page: 1,
        search: value
      }));
    }, 500);
    
    return () => clearTimeout(timeoutId);
  };

  // Reset all filters now applies directly
  const resetFilters = () => {
    // Reset local state
    setSearchTerm('');
    setFilters({
      operatingSystem: '',
      location: '',
      applicationOwner: '',
      status: ''
    });
    
    // Reset query params - this will trigger a fetch via useEffect
    setQueryParams({
      page: 1,
      size: itemsPerPage,
      search: '',
      operatingSystem: '',
      location: '',
      applicationOwner: '',
      status: '',
      sort: 'hostname',
      order: 'asc'
    });
  };

  // Pagination logic
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;

  // Update paginate function
  const paginate = (pageNumber: number) => {
    setQueryParams(prev => ({
      ...prev,
      page: pageNumber
    }));
  };

  // Handle items per page change
  const handleItemsPerPageChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
    const newSize = Number(event.target.value);
    setItemsPerPage(newSize);
    setQueryParams(prev => ({
      ...prev,
      size: newSize,
      page: 1
    }));
  };

  const handleDownloadTemplate = async () => {
    try {
      const response = await serverApi.getImportTemplate();
      
      // Create a download link and trigger it
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'server_import_template.xlsx');
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      console.error('Error downloading template:', err);
      setFileUploadError('Failed to download template. Please try again.');
      setTimeout(() => setFileUploadError(null), 5000);
    }
  };

  const handleExport = async () => {
    try {
      // Build export parameters based on current filters
      const params: any = {};
      if (searchTerm) params.search = searchTerm;
      if (filters.operatingSystem) params.operatingSystem = filters.operatingSystem;
      if (filters.location) params.location = filters.location;
      if (filters.applicationOwner) params.applicationOwner = filters.applicationOwner;
      if (filters.status) params.status = filters.status;
      
      const response = await serverApi.exportServers(params);
      
      // Create a download link and trigger it
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'servers_export.xlsx');
      document.body.appendChild(link);
      link.click();
      link.remove();
      
      setFileUploadSuccess('Servers exported successfully!');
      setTimeout(() => setFileUploadSuccess(null), 3000);
    } catch (err) {
      console.error('Error exporting servers:', err);
      setFileUploadError('Failed to export servers. Please try again.');
      setTimeout(() => setFileUploadError(null), 5000);
    }
  };

  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    if (!event.target.files || event.target.files.length === 0) {
      return;
    }
    
    const file = event.target.files[0];
    const formData = new FormData();
    formData.append('file', file);
    
    try {
      setLoading(true);
      
      const response = await serverApi.importServers(formData);
      
      setFileUploadSuccess(`Import successful: ${response.data.success} servers added, ${response.data.failed} failed.`);
      
      // Refresh server list
      fetchServers(currentPage, itemsPerPage, queryParams.sort, queryParams.order, filters);
      
    } catch (err: any) {
      console.error('Error uploading file:', err);
      setFileUploadError(err.response?.data?.message || 'Failed to import servers. Please try again.');
    } finally {
      setLoading(false);
      // Reset file input
      event.target.value = '';
    }
  };

  // New function to handle form input changes
  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setNewServerData(prev => ({ ...prev, [name]: value }));
  };

  // New function to handle server creation
  const handleAddServer = async (e: React.FormEvent) => {
    e.preventDefault();
    
    try {
      // Format data for backend
      const serverData = {
        ...newServerData,
        ip: newServerData.ipAddress // Map ipAddress to ip for backend
      };
      
      // Send to backend
      await serverApi.createServer(serverData);
      
      // Refresh the server list
      fetchServers(currentPage, itemsPerPage, queryParams.sort, queryParams.order, filters);
      
      // Close modal and reset form
      setShowAddModal(false);
      setNewServerData({
        hostname: '',
        ipAddress: '',
        operatingSystem: '',
        serverRole: '',
        serverType: '',
        applicationName: '',
        applicationSPOC: '',
        applicationOwner: '',
        platform: '',
        location: '',
        manufacturer: '',
        ram: '',
        cpu: '',
        status: 'new'
      });
      
      // Show success message
      setFileUploadSuccess('Server added successfully');
      setTimeout(() => setFileUploadSuccess(null), 3000);
    } catch (err) {
      console.error('Error adding server:', err);
      setFileUploadError('Failed to add server. Please try again.');
      setTimeout(() => setFileUploadError(null), 5000);
    }
  };

  const handleClearAllServers = async () => {
    try {
      setLoading(true);
      await serverApi.clearAllServers();
      setFileUploadSuccess('All servers have been cleared successfully');
      setShowClearAllConfirmation(false);
      fetchServers(currentPage, itemsPerPage, queryParams.sort, queryParams.order, filters); // Refresh the server list (should be empty now)
    } catch (err) {
      console.error('Error clearing servers:', err);
      setFileUploadError('Failed to clear servers. Please try again.');
    } finally {
      setLoading(false);
      setTimeout(() => setFileUploadSuccess(null), 3000);
    }
  };

  // Column resize handlers
  const handleResizeStart = (e: React.MouseEvent, columnKey: string) => {
    e.preventDefault();
    resizingColumn.current = columnKey;
    startX.current = e.clientX;
    startWidth.current = columnWidths[columnKey] || defaultColumnWidths[columnKey];
    
    document.addEventListener('mousemove', handleResizeMove);
    document.addEventListener('mouseup', handleResizeEnd);
  };
  
  const handleResizeMove = (e: MouseEvent) => {
    if (!resizingColumn.current) return;
    
    const diff = e.clientX - startX.current;
    const newWidth = Math.max(50, startWidth.current + diff); // Minimum width of 50px
    
    setColumnWidths(prev => ({
      ...prev,
      [resizingColumn.current!]: newWidth
    }));
  };
  
  const handleResizeEnd = () => {
    resizingColumn.current = null;
    document.removeEventListener('mousemove', handleResizeMove);
    document.removeEventListener('mouseup', handleResizeEnd);
  };

  if (loading) {
    return (
      <div className="flex h-full min-h-[400px] items-center justify-center">
        <div className="h-12 w-12 animate-spin rounded-full border-4 border-primary-600 border-t-transparent"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 dark:bg-red-900/30 border border-red-400 text-red-700 dark:text-red-400 px-4 py-3 rounded mb-4" role="alert">
        <span className="block sm:inline">{error}</span>
      </div>
    );
  }

  // Generate filter options dynamically
  const filterOptions = getFilterOptions();

  return (
    <div className="relative">
      <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-8">Server Inventory</h1>
      
      {/* Search and filter controls */}
      <div className="mb-6 bg-white dark:bg-gray-800 p-4 rounded-lg shadow">
        <div className="flex flex-col gap-4 md:flex-row md:items-center md:justify-between">
          {/* Search input - update to use new handler */}
          <div className="w-full md:w-1/3">
            <div className="relative">
              <input
                type="text"
                placeholder="Search servers..."
                className="pl-10 pr-4 py-2 w-full border border-gray-300 dark:border-gray-700 rounded-md dark:bg-gray-700 dark:text-white"
                value={searchTerm}
                onChange={handleSearchChange}
              />
              <MagnifyingGlassIcon className="absolute left-3 top-2.5 h-5 w-5 text-gray-400" />
            </div>
          </div>
          
          <div className="flex flex-wrap gap-2">
            {/* Remove Apply Filters button since we're applying filters directly */}
            
            {/* Add Server button */}
            <button 
              className="inline-flex items-center px-4 py-2 bg-green-600 border border-transparent rounded-md font-semibold text-xs text-white tracking-widest hover:bg-green-700"
              onClick={() => setShowAddModal(true)}
            >
              <PlusIcon className="h-4 w-4 mr-2" />
              Add Server
            </button>
            
            {/* Import/Export buttons */}
            <div className="flex space-x-2">
              <label className="inline-flex items-center px-4 py-2 bg-primary-600 border border-transparent rounded-md font-semibold text-xs text-white tracking-widest hover:bg-primary-700 cursor-pointer">
                <ArrowUpTrayIcon className="h-4 w-4 mr-2" />
                Import
                <input type="file" className="hidden" onChange={handleFileUpload} accept=".xlsx,.xls,.csv" />
              </label>
              
              <button 
                className="inline-flex items-center px-4 py-2 bg-primary-600 border border-transparent rounded-md font-semibold text-xs text-white tracking-widest hover:bg-primary-700"
                onClick={handleExport}
              >
                <ArrowDownTrayIcon className="h-4 w-4 mr-2" />
                Export
              </button>
              
              <button 
                className="inline-flex items-center px-4 py-2 bg-gray-600 border border-transparent rounded-md font-semibold text-xs text-white tracking-widest hover:bg-gray-700"
                onClick={handleDownloadTemplate}
              >
                <DocumentArrowDownIcon className="h-4 w-4 mr-2" />
                Template
              </button>

              {/* Clear All Servers button - Admin Only */}
              {isAdmin() && (
                <button 
                  className="inline-flex items-center px-4 py-2 bg-red-600 border border-transparent rounded-md font-semibold text-xs text-white tracking-widest hover:bg-red-700"
                  onClick={() => setShowClearAllConfirmation(true)}
                >
                  <TrashIcon className="h-4 w-4 mr-2" />
                  Clear All
                </button>
              )}
            </div>
          </div>
        </div>
        
        {/* Filter dropdowns */}
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-4 mt-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">Operating System</label>
            <select 
              className="w-full border border-gray-300 dark:border-gray-700 rounded-md p-2 dark:bg-gray-700 dark:text-white"
              value={filters.operatingSystem}
              onChange={(e) => handleFilterChange('operatingSystem', e.target.value)}
            >
              <option value="">All Operating Systems</option>
              {filterOptions.operatingSystems.map((os) => (
                <option key={os} value={os}>{os}</option>
              ))}
            </select>
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">Location</label>
            <select 
              className="w-full border border-gray-300 dark:border-gray-700 rounded-md p-2 dark:bg-gray-700 dark:text-white"
              value={filters.location}
              onChange={(e) => handleFilterChange('location', e.target.value)}
            >
              <option value="">All Locations</option>
              {filterOptions.locations.map((location) => (
                <option key={location} value={location}>{location}</option>
              ))}
            </select>
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">Application Owner</label>
            <select 
              className="w-full border border-gray-300 dark:border-gray-700 rounded-md p-2 dark:bg-gray-700 dark:text-white"
              value={filters.applicationOwner}
              onChange={(e) => handleFilterChange('applicationOwner', e.target.value)}
            >
              <option value="">All Application Owners</option>
              {filterOptions.applicationOwners.map((owner) => (
                <option key={owner} value={owner}>{owner}</option>
              ))}
            </select>
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">Status</label>
            <select 
              className="w-full border border-gray-300 dark:border-gray-700 rounded-md p-2 dark:bg-gray-700 dark:text-white"
              value={filters.status}
              onChange={(e) => handleFilterChange('status', e.target.value)}
            >
              <option value="">All Statuses</option>
              {filterOptions.statuses.map((status) => (
                <option key={status} value={status}>{status}</option>
              ))}
            </select>
          </div>
        </div>
        
        {/* Reset filters button */}
        <div className="mt-4 flex justify-end">
          <button 
            className="px-4 py-2 bg-gray-200 dark:bg-gray-700 border border-transparent rounded-md font-semibold text-xs text-gray-700 dark:text-white tracking-widest hover:bg-gray-300 dark:hover:bg-gray-600"
            onClick={resetFilters}
          >
            Reset Filters
          </button>
        </div>

        {/* Upload status messages */}
        {fileUploadError && (
          <div className="mt-4 p-3 bg-red-100 dark:bg-red-900/30 border border-red-400 text-red-700 dark:text-red-400 rounded">
            {fileUploadError}
          </div>
        )}
        
        {fileUploadSuccess && (
          <div className="mt-4 p-3 bg-green-100 dark:bg-green-900/30 border border-green-400 text-green-700 dark:text-green-400 rounded">
            {fileUploadSuccess}
          </div>
        )}
      </div>
      
      {/* Server table with fixed layout and sticky header */}
      <div className="bg-white dark:bg-gray-800 shadow overflow-hidden sm:rounded-lg">
        <div className="overflow-x-auto" style={{ position: 'relative' }}>
          <table 
            ref={tableRef}
            className="min-w-full divide-y divide-gray-200 dark:divide-gray-700"
            style={{ tableLayout: 'fixed' }}
          >
            <thead className="bg-gray-50 dark:bg-gray-700" style={{ position: 'sticky', top: 0, zIndex: 10 }}>
              <tr>
                {Object.entries({
                  hostname: 'Hostname',
                  ip: 'IP Address',
                  operatingSystem: 'OS',
                  serverRole: 'Role',
                  serverType: 'Type',
                  applicationName: 'Application',
                  applicationSPOC: 'App SPOC',
                  applicationOwner: 'App Owner',
                  location: 'Location',
                  manufacturer: 'Manufacturer',
                  ram: 'RAM',
                  cpu: 'CPU',
                  status: 'Status'
                }).map(([key, label]) => (
                  <th 
                    key={key}
                    scope="col" 
                    className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider cursor-pointer relative"
                    style={{ width: `${columnWidths[key]}px`, minWidth: `${columnWidths[key]}px` }}
                    onClick={() => handleSort(key as keyof Server)}
                  >
                    <div className="flex items-center">
                      {label}
                      {getSortIcon(key as keyof Server)}
                    </div>
                    {/* Column resizer */}
                    <div 
                      className="absolute top-0 right-0 h-full w-4 cursor-col-resize hover:bg-blue-400 hover:opacity-50"
                      onMouseDown={(e) => handleResizeStart(e, key)}
                    />
                  </th>
                ))}
              </tr>
            </thead>
            <tbody className="bg-white dark:bg-gray-800 divide-y divide-gray-200 dark:divide-gray-700">
              {filteredServers.length > 0 ? (
                filteredServers.map((server) => (
                  <tr key={server.id}>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900 dark:text-white overflow-hidden text-ellipsis" style={{ width: columnWidths.hostname }}>
                      {server.hostname}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-300 overflow-hidden text-ellipsis" style={{ width: columnWidths.ip }}>
                      {server.ipAddress || server.ip}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-300 overflow-hidden text-ellipsis" style={{ width: columnWidths.operatingSystem }}>
                      {server.operatingSystem}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-300 overflow-hidden text-ellipsis" style={{ width: columnWidths.serverRole }}>
                      {server.serverRole}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-300 overflow-hidden text-ellipsis" style={{ width: columnWidths.serverType }}>
                      {server.serverType}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-300 overflow-hidden text-ellipsis" style={{ width: columnWidths.applicationName }}>
                      {server.applicationName}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-300 overflow-hidden text-ellipsis" style={{ width: columnWidths.applicationSPOC }}>
                      {server.applicationSPOC}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-300 overflow-hidden text-ellipsis" style={{ width: columnWidths.applicationOwner }}>
                      {server.applicationOwner}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-300 overflow-hidden text-ellipsis" style={{ width: columnWidths.location }}>
                      {server.location}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-300 overflow-hidden text-ellipsis" style={{ width: columnWidths.manufacturer }}>
                      {server.manufacturer}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-300 overflow-hidden text-ellipsis" style={{ width: columnWidths.ram }}>
                      {server.ram}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-300 overflow-hidden text-ellipsis" style={{ width: columnWidths.cpu }}>
                      {server.cpu}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm overflow-hidden text-ellipsis" style={{ width: columnWidths.status }}>
                      <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full 
                        ${server.status === 'live' ? 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200' : 
                          server.status === 'shutdown' ? 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200' : 
                          'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200'}`}>
                        {server.status}
                      </span>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan={13} className="px-6 py-4 text-center text-sm text-gray-500 dark:text-gray-300">
                    No servers found
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
        
        {/* Pagination controls */}
        <div className="px-6 py-3 flex items-center justify-between border-t border-gray-200 dark:border-gray-700 bg-gray-50 dark:bg-gray-700">
          <div className="flex-1 flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-700 dark:text-gray-300">
                Showing <span className="font-medium">{Math.min(indexOfFirstItem + 1, totalItems)}</span> to{' '}
                <span className="font-medium">
                  {Math.min(indexOfLastItem, totalItems)}
                </span>{' '}
                of <span className="font-medium">{totalItems}</span> results
              </p>
            </div>
            <div className="flex items-center space-x-4">
              <select
                className="border border-gray-300 dark:border-gray-600 rounded-md p-2 dark:bg-gray-800 dark:text-white"
                value={itemsPerPage}
                onChange={handleItemsPerPageChange}
              >
                <option value={5}>5 per page</option>
                <option value={10}>10 per page</option>
                <option value={25}>25 per page</option>
                <option value={50}>50 per page</option>
              </select>
              <nav className="relative z-0 inline-flex rounded-md shadow-sm -space-x-px" aria-label="Pagination">
                <button
                  onClick={() => paginate(Math.max(1, currentPage - 1))}
                  className="relative inline-flex items-center px-2 py-2 rounded-l-md border border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-800 text-sm font-medium text-gray-500 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700"
                  disabled={currentPage === 1}
                >
                  <span className="sr-only">Previous</span>
                  &larr;
                </button>
                {Array.from({ length: Math.min(5, totalPages) }).map((_, idx) => {
                  const pageToShow = currentPage <= 3 
                    ? idx + 1 
                    : currentPage >= totalPages - 2 
                      ? totalPages - 4 + idx 
                      : currentPage - 2 + idx;
                  
                  if (pageToShow > 0 && pageToShow <= totalPages) {
                    return (
                      <button
                        key={pageToShow}
                        onClick={() => paginate(pageToShow)}
                        className={`relative inline-flex items-center px-4 py-2 border ${
                          currentPage === pageToShow
                            ? 'bg-primary-600 text-white z-10 border-primary-600'
                            : 'bg-white dark:bg-gray-800 text-gray-700 dark:text-gray-300 border-gray-300 dark:border-gray-600 hover:bg-gray-50 dark:hover:bg-gray-700'
                        }`}
                      >
                        {pageToShow}
                      </button>
                    );
                  }
                  return null;
                })}
                <button
                  onClick={() => paginate(Math.min(totalPages, currentPage + 1))}
                  className="relative inline-flex items-center px-2 py-2 rounded-r-md border border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-800 text-sm font-medium text-gray-500 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700"
                  disabled={currentPage === totalPages}
                >
                  <span className="sr-only">Next</span>
                  &rarr;
                </button>
              </nav>
            </div>
          </div>
        </div>
      </div>

      {/* Add Server Modal */}
      {showAddModal && (
        <div className="fixed inset-0 z-50 overflow-y-auto">
          <div className="flex items-end justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
            <div className="fixed inset-0 transition-opacity" aria-hidden="true">
              <div className="absolute inset-0 bg-gray-500 opacity-75"></div>
            </div>
            <span className="hidden sm:inline-block sm:align-middle sm:h-screen" aria-hidden="true">&#8203;</span>
            <div className="inline-block align-bottom bg-white dark:bg-gray-800 rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full">
              <div className="bg-white dark:bg-gray-800 px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
                <div className="sm:flex sm:items-start">
                  <div className="mt-3 text-center sm:mt-0 sm:ml-4 sm:text-left w-full">
                    <h3 className="text-lg leading-6 font-medium text-gray-900 dark:text-white flex justify-between">
                      Add New Server
                      <button onClick={() => setShowAddModal(false)} className="text-gray-400 hover:text-gray-500">
                        <XMarkIcon className="h-6 w-6" />
                      </button>
                    </h3>
                    <div className="mt-4">
                      <form onSubmit={handleAddServer} className="space-y-4">
                        {/* Basic Info */}
                        <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
                          <div>
                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                              Hostname*
                            </label>
                            <input
                              type="text"
                              name="hostname"
                              required
                              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md dark:bg-gray-700 dark:text-white"
                              value={newServerData.hostname}
                              onChange={handleInputChange}
                            />
                          </div>
                          <div>
                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                              IP Address*
                            </label>
                            <input
                              type="text"
                              name="ipAddress"
                              required
                              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md dark:bg-gray-700 dark:text-white"
                              value={newServerData.ipAddress}
                              onChange={handleInputChange}
                            />
                          </div>
                        </div>
                        
                        {/* Server Details */}
                        <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
                          <div>
                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                              Operating System*
                            </label>
                            <input
                              type="text"
                              name="operatingSystem"
                              required
                              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md dark:bg-gray-700 dark:text-white"
                              value={newServerData.operatingSystem}
                              onChange={handleInputChange}
                            />
                          </div>
                          <div>
                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                              Server Role*
                            </label>
                            <input
                              type="text"
                              name="serverRole"
                              required
                              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md dark:bg-gray-700 dark:text-white"
                              value={newServerData.serverRole}
                              onChange={handleInputChange}
                            />
                          </div>
                        </div>
                        
                        <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
                          <div>
                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                              Server Type*
                            </label>
                            <input
                              type="text"
                              name="serverType"
                              required
                              placeholder="Physical, Virtual, Container, etc."
                              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md dark:bg-gray-700 dark:text-white"
                              value={newServerData.serverType}
                              onChange={handleInputChange}
                            />
                          </div>
                          <div>
                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                              Platform
                            </label>
                            <input
                              type="text"
                              name="platform"
                              placeholder="VMware, AWS, Bare Metal, etc."
                              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md dark:bg-gray-700 dark:text-white"
                              value={newServerData.platform}
                              onChange={handleInputChange}
                            />
                          </div>
                        </div>
                        
                        {/* Application Info */}
                        <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
                          <div>
                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                              Application Name*
                            </label>
                            <input
                              type="text"
                              name="applicationName"
                              required
                              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md dark:bg-gray-700 dark:text-white"
                              value={newServerData.applicationName}
                              onChange={handleInputChange}
                            />
                          </div>
                          <div>
                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                              Application SPOC
                            </label>
                            <input
                              type="text"
                              name="applicationSPOC"
                              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md dark:bg-gray-700 dark:text-white"
                              value={newServerData.applicationSPOC}
                              onChange={handleInputChange}
                            />
                          </div>
                        </div>
                        
                        <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
                          <div>
                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                              Application Owner*
                            </label>
                            <input
                              type="text"
                              name="applicationOwner"
                              required
                              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md dark:bg-gray-700 dark:text-white"
                              value={newServerData.applicationOwner}
                              onChange={handleInputChange}
                            />
                          </div>
                          <div>
                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                              Location*
                            </label>
                            <input
                              type="text"
                              name="location"
                              required
                              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md dark:bg-gray-700 dark:text-white"
                              value={newServerData.location}
                              onChange={handleInputChange}
                            />
                          </div>
                        </div>
                        
                        {/* Hardware Info */}
                        <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
                          <div>
                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                              Manufacturer
                            </label>
                            <input
                              type="text"
                              name="manufacturer"
                              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md dark:bg-gray-700 dark:text-white"
                              value={newServerData.manufacturer}
                              onChange={handleInputChange}
                            />
                          </div>
                          <div>
                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                              RAM
                            </label>
                            <input
                              type="text"
                              name="ram"
                              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md dark:bg-gray-700 dark:text-white"
                              value={newServerData.ram}
                              onChange={handleInputChange}
                            />
                          </div>
                        </div>
                        
                        <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
                          <div>
                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                              CPU
                            </label>
                            <input
                              type="text"
                              name="cpu"
                              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md dark:bg-gray-700 dark:text-white"
                              value={newServerData.cpu}
                              onChange={handleInputChange}
                            />
                          </div>
                          <div>
                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                              Status*
                            </label>
                            <select
                              name="status"
                              required
                              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md dark:bg-gray-700 dark:text-white"
                              value={newServerData.status}
                              onChange={handleInputChange}
                            >
                              <option value="live">Live</option>
                              <option value="shutdown">Shutdown</option>
                              <option value="new">New</option>
                            </select>
                          </div>
                        </div>
                        
                        <div className="mt-5 sm:mt-4 sm:flex sm:flex-row-reverse">
                          <button
                            type="submit"
                            className="w-full inline-flex justify-center rounded-md border border-transparent shadow-sm px-4 py-2 bg-green-600 text-base font-medium text-white hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500 sm:ml-3 sm:w-auto sm:text-sm"
                          >
                            Add Server
                          </button>
                          <button
                            type="button"
                            onClick={() => setShowAddModal(false)}
                            className="mt-3 w-full inline-flex justify-center rounded-md border border-gray-300 dark:border-gray-600 shadow-sm px-4 py-2 bg-white dark:bg-gray-700 text-base font-medium text-gray-700 dark:text-gray-200 hover:bg-gray-50 dark:hover:bg-gray-600 focus:outline-none sm:mt-0 sm:w-auto sm:text-sm"
                          >
                            Cancel
                          </button>
                        </div>
                      </form>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Clear All Confirmation Modal */}
      {showClearAllConfirmation && (
        <div className="fixed inset-0 z-10 overflow-y-auto">
          <div className="flex min-h-screen items-center justify-center px-4 text-center">
            <div className="fixed inset-0 bg-black bg-opacity-50 transition-opacity"></div>
            
            <div className="inline-block transform overflow-hidden rounded-lg bg-white dark:bg-gray-800 text-left align-bottom shadow-xl transition-all sm:my-8 sm:w-full sm:max-w-lg sm:align-middle">
              <div className="bg-white dark:bg-gray-800 p-6">
                <div className="sm:flex sm:items-start">
                  <div className="mx-auto flex h-12 w-12 flex-shrink-0 items-center justify-center rounded-full bg-red-100 dark:bg-red-900/30 sm:mx-0 sm:h-10 sm:w-10">
                    <TrashIcon className="h-6 w-6 text-red-600 dark:text-red-400" />
                  </div>
                  <div className="mt-3 text-center sm:mt-0 sm:ml-4 sm:text-left">
                    <h3 className="text-lg font-medium leading-6 text-gray-900 dark:text-white">
                      Clear All Servers
                    </h3>
                    <div className="mt-2">
                      <p className="text-sm text-gray-500 dark:text-gray-400">
                        Are you sure you want to clear all servers? This action cannot be undone.
                      </p>
                    </div>
                  </div>
                </div>
                <div className="mt-5 sm:mt-4 sm:flex sm:flex-row-reverse">
                  <button
                    type="button"
                    className="inline-flex w-full justify-center rounded-md border border-transparent bg-red-600 px-4 py-2 text-base font-medium text-white shadow-sm hover:bg-red-700 sm:ml-3 sm:w-auto sm:text-sm"
                    onClick={handleClearAllServers}
                  >
                    Clear All
                  </button>
                  <button
                    type="button"
                    className="mt-3 inline-flex w-full justify-center rounded-md border border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-700 px-4 py-2 text-base font-medium text-gray-700 dark:text-gray-300 shadow-sm hover:bg-gray-50 dark:hover:bg-gray-600 sm:mt-0 sm:w-auto sm:text-sm"
                    onClick={() => setShowClearAllConfirmation(false)}
                  >
                    Cancel
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Add cursor styling when resizing columns */}
      {resizingColumn.current && (
        <div 
          style={{ 
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            zIndex: 1000,
            cursor: 'col-resize',
            userSelect: 'none'
          }} 
        />
      )}
    </div>
  );
};

export default ServerInventory;
